contracts/MultiSigWallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title Multi-Signature Wallet
 * @dev Simple multi-signature wallet for fund management
 */
contract MultiSigWallet is Ownable {

    struct Transaction {
        address to;
        uint256 value;
        bytes data;
        bool executed;
        uint256 confirmations;
    }

    mapping(uint256 => Transaction) public transactions;
    mapping(uint256 => mapping(address => bool)) public confirmations;
    mapping(address => bool) public isOwner;

    address[] public owners;
    uint256 public required;
    uint256 public transactionCount;

    event Confirmation(address indexed sender, uint256 indexed transactionId);
    event Execution(uint256 indexed transactionId);
    event SubmitTransaction(uint256 indexed transactionId);

    modifier onlyWallet() {
        require(msg.sender == address(this), "Not wallet");
        _;
    }

    modifier ownerExists(address owner) {
        require(isOwner[owner], "Not owner");
        _;
    }

    modifier transactionExists(uint256 transactionId) {
        require(transactions[transactionId].to != address(0), "Transaction not exists");
        _;
    }

    modifier confirmed(uint256 transactionId, address owner) {
        require(confirmations[transactionId][owner], "Not confirmed");
        _;
    }

    modifier notConfirmed(uint256 transactionId, address owner) {
        require(!confirmations[transactionId][owner], "Already confirmed");
        _;
    }

    modifier notExecuted(uint256 transactionId) {
        require(!transactions[transactionId].executed, "Already executed");
        _;
    }

    constructor(address[] memory _owners, uint256 _required) Ownable(msg.sender) {
        require(_owners.length > 0, "Owners required");
        require(_required > 0 && _required <= _owners.length, "Invalid required number");

        for (uint256 i = 0; i < _owners.length; i++) {
            address owner = _owners[i];
            require(owner != address(0), "Invalid owner");
            require(!isOwner[owner], "Owner not unique");

            isOwner[owner] = true;
            owners.push(owner);
        }

        required = _required;
    }

    function submitTransaction(address to, uint256 value, bytes memory data)
        public
        ownerExists(msg.sender)
        returns (uint256 transactionId)
    {
        transactionId = transactionCount;
        transactions[transactionId] = Transaction({
            to: to,
            value: value,
            data: data,
            executed: false,
            confirmations: 0
        });
        transactionCount += 1;
        emit SubmitTransaction(transactionId);
    }

    function confirmTransaction(uint256 transactionId)
        public
        ownerExists(msg.sender)
        transactionExists(transactionId)
        notConfirmed(transactionId, msg.sender)
    {
        confirmations[transactionId][msg.sender] = true;
        transactions[transactionId].confirmations += 1;
        emit Confirmation(msg.sender, transactionId);
        executeTransaction(transactionId);
    }

    function executeTransaction(uint256 transactionId)
        public
        notExecuted(transactionId)
    {
        if (isConfirmed(transactionId)) {
            Transaction storage txn = transactions[transactionId];
            txn.executed = true;
            (bool success, ) = txn.to.call{value: txn.value}(txn.data);
            require(success, "Transaction failed");
            emit Execution(transactionId);
        }
    }

    function isConfirmed(uint256 transactionId) public view returns (bool) {
        return transactions[transactionId].confirmations >= required;
    }

    receive() external payable {}
}

contracts/XpSwapDEX.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title XpSwap DEX Main Router
 * @dev Core DEX contract for token swapping and liquidity management
 */
contract XpSwapDEX is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // Pool structure
    struct Pool {
        address tokenA;
        address tokenB;
        uint256 reserveA;
        uint256 reserveB;
        uint256 totalLiquidity;
        uint256 feeRate; // in basis points
        bool active;
    }

    mapping(bytes32 => Pool) public pools;
    bytes32[] public poolIds;

    uint256 public constant MINIMUM_LIQUIDITY = 1000;
    uint256 public constant MAX_FEE_RATE = 1000; // 10%

    event PoolCreated(bytes32 indexed poolId, address indexed tokenA, address indexed tokenB);
    event Swap(address indexed user, address tokenIn, address tokenOut, uint256 amountIn, uint256 amountOut);
    event LiquidityAdded(address indexed user, bytes32 indexed poolId, uint256 amountA, uint256 amountB, uint256 liquidity);

    constructor() Ownable(msg.sender) {}

    function createPool(address tokenA, address tokenB, uint256 feeRate) external onlyOwner returns (bytes32 poolId) {
        require(tokenA != tokenB, "Identical tokens");
        require(tokenA != address(0) && tokenB != address(0), "Zero address");
        require(feeRate <= MAX_FEE_RATE, "Fee rate too high");

        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        poolId = keccak256(abi.encodePacked(token0, token1));

        require(pools[poolId].tokenA == address(0), "Pool exists");

        pools[poolId] = Pool({
            tokenA: token0,
            tokenB: token1,
            reserveA: 0,
            reserveB: 0,
            totalLiquidity: 0,
            feeRate: feeRate,
            active: true
        });

        poolIds.push(poolId);
        emit PoolCreated(poolId, token0, token1);
    }

    function getPoolCount() external view returns (uint256) {
        return poolIds.length;
    }
}

contracts/XPSwapFlashLoanSecurity.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title XPSwap Flash Loan Security
 * @dev Flash loan functionality with enhanced security
 */
contract XPSwapFlashLoanSecurity is ReentrancyGuard, Ownable {

    struct FlashLoanData {
        address asset;
        uint256 amount;
        uint256 fee;
        address borrower;
    }

    mapping(address => bool) public authorizedCallers;
    mapping(address => uint256) public cooldowns;

    uint256 public constant FLASH_LOAN_FEE = 9; // 0.09%
    uint256 public constant COOLDOWN_PERIOD = 10 minutes;

    event FlashLoan(address indexed borrower, address indexed asset, uint256 amount, uint256 fee);

    constructor() Ownable(msg.sender) {}

    function executeFlashLoan(
        address asset,
        uint256 amount,
        bytes calldata data
    ) external nonReentrant {
        require(authorizedCallers[msg.sender], "Unauthorized");
        require(block.timestamp >= cooldowns[msg.sender] + COOLDOWN_PERIOD, "Cooldown active");

        uint256 fee = (amount * FLASH_LOAN_FEE) / 10000;
        uint256 balanceBefore = IERC20(asset).balanceOf(address(this));

        // Transfer tokens to borrower
        IERC20(asset).transfer(msg.sender, amount);

        // Execute borrower's logic
        (bool success, ) = msg.sender.call(data);
        require(success, "Flash loan execution failed");

        // Check repayment
        uint256 balanceAfter = IERC20(asset).balanceOf(address(this));
        require(balanceAfter >= balanceBefore + fee, "Flash loan not repaid");

        cooldowns[msg.sender] = block.timestamp;

        emit FlashLoan(msg.sender, asset, amount, fee);
    }

    function setAuthorizedCaller(address caller, bool authorized) external onlyOwner {
        authorizedCallers[caller] = authorized;
    }
}

contracts/XPSwapMEVProtection.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title XPSwap MEV Protection
 * @dev Advanced MEV protection mechanisms
 */
contract XPSwapMEVProtection is ReentrancyGuard, Ownable {

    struct MEVProtection {
        uint256 lastTxBlock;
        uint256 txCountInBlock;
        uint256 lastTxTimestamp;
        bool flagged;
    }

    mapping(address => MEVProtection) public mevProtection;

    uint256 public constant MAX_TXS_PER_BLOCK = 3;
    uint256 public constant MEV_PROTECTION_BLOCKS = 2;

    event MEVDetected(address indexed user, uint256 blockNumber, string reason);

    constructor() Ownable(msg.sender) {}

    function checkMEVProtection(address user, uint256 amount) external view returns (bool) {
        MEVProtection memory protection = mevProtection[user];

        if (block.number == protection.lastTxBlock) {
            if (protection.txCountInBlock >= MAX_TXS_PER_BLOCK) {
                return false;
            }
        }

        return !protection.flagged;
    }

    function updateMEVProtection(address user) external {
        MEVProtection storage protection = mevProtection[user];

        if (block.number > protection.lastTxBlock) {
            protection.txCountInBlock = 1;
        } else {
            protection.txCountInBlock++;
        }

        protection.lastTxBlock = block.number;
        protection.lastTxTimestamp = block.timestamp;
    }
}

contracts/XPSwapOptionsSecurity.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title XPSwap Options Security
 * @dev Security layer for options trading
 */
contract XPSwapOptionsSecurity is ReentrancyGuard, Ownable {

    struct OptionSecurity {
        uint256 maxStrike;
        uint256 minStrike;
        uint256 maxExpiry;
        uint256 minExpiry;
        bool isActive;
    }

    mapping(address => OptionSecurity) public optionParams;
    mapping(address => bool) public authorizedTraders;

    event OptionSecurityUpdated(address indexed asset, uint256 maxStrike, uint256 minStrike);

    constructor() Ownable(msg.sender) {}

    function validateOption(
        address underlying,
        uint256 strike,
        uint256 expiry
    ) external view returns (bool) {
        OptionSecurity memory params = optionParams[underlying];

        if (!params.isActive) return false;
        if (strike < params.minStrike || strike > params.maxStrike) return false;
        if (expiry < params.minExpiry || expiry > params.maxExpiry) return false;

        return true;
    }

    function setOptionParams(
        address underlying,
        uint256 maxStrike,
        uint256 minStrike,
        uint256 maxExpiry,
        uint256 minExpiry,
        bool isActive
    ) external onlyOwner {
        optionParams[underlying] = OptionSecurity({
            maxStrike: maxStrike,
            minStrike: minStrike,
            maxExpiry: maxExpiry,
            minExpiry: minExpiry,
            isActive: isActive
        });

        emit OptionSecurityUpdated(underlying, maxStrike, minStrike);
    }
}

contracts/XpSwapToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title XpSwap Token (XPS)
 * @dev Native token for XpSwap DEX with deflationary mechanisms
 * Features:
 * - Deflationary tokenomics through burning
 * - Staking rewards
 * - LP mining rewards
 * - Fee discounts for holders
 * - Governance capabilities
 * - Bug bounty and marketing allocations
 */
contract XpSwapToken is ERC20, ERC20Burnable, Pausable, Ownable, ReentrancyGuard {
    // Maximum total supply: 1 billion tokens
    uint256 public constant MAX_SUPPLY = 1_000_000_000 * 10**18;

    // Allocation percentages (basis points, 10000 = 100%)
    uint256 public constant TEAM_ALLOCATION = 1500;        // 15% for team
    uint256 public constant DEVELOPMENT_ALLOCATION = 1000;  // 10% for development
    uint256 public constant MARKETING_ALLOCATION = 500;     // 5% for marketing
    uint256 public constant BUG_BOUNTY_ALLOCATION = 200;    // 2% for bug bounty
    uint256 public constant LIQUIDITY_MINING = 4000;       // 40% for liquidity mining
    uint256 public constant STAKING_REWARDS = 2000;        // 20% for staking rewards
    uint256 public constant RESERVE_ALLOCATION = 800;      // 8% for reserves

    // Addresses for allocations
    address public teamWallet;
    address public developmentWallet;
    address public marketingWallet;
    address public bugBountyWallet;
    address public stakingContract;
    address public liquidityMiningContract;
    address public reserveWallet;

    // Fee discount structure (basis points)
    uint256 public constant BASE_FEE = 30; // 0.3%
    mapping(uint256 => uint256) public feeDiscountTiers; // XPS amount => discount percentage

    // Burn tracker
    uint256 public totalBurned;

    // Minting limits per category
    mapping(string => uint256) public categoryMinted;
    mapping(string => uint256) public categoryLimit;

    // Events
    event TokensBurned(uint256 amount, string reason);
    event FeeDiscountApplied(address user, uint256 xpsBalance, uint256 discountPercent);
    event AllocationMinted(string category, address recipient, uint256 amount);

    constructor(
        address _teamWallet,
        address _developmentWallet,
        address _marketingWallet,
        address _bugBountyWallet
    ) ERC20("XpSwap Token", "XPS") Ownable(msg.sender) {
        require(_teamWallet != address(0), "Invalid team wallet");
        require(_developmentWallet != address(0), "Invalid development wallet");
        require(_marketingWallet != address(0), "Invalid marketing wallet");
        require(_bugBountyWallet != address(0), "Invalid bug bounty wallet");

        teamWallet = _teamWallet;
        developmentWallet = _developmentWallet;
        marketingWallet = _marketingWallet;
        bugBountyWallet = _bugBountyWallet;
        reserveWallet = owner();

        // Set category limits
        categoryLimit["team"] = (MAX_SUPPLY * TEAM_ALLOCATION) / 10000;
        categoryLimit["development"] = (MAX_SUPPLY * DEVELOPMENT_ALLOCATION) / 10000;
        categoryLimit["marketing"] = (MAX_SUPPLY * MARKETING_ALLOCATION) / 10000;
        categoryLimit["bugBounty"] = (MAX_SUPPLY * BUG_BOUNTY_ALLOCATION) / 10000;
        categoryLimit["liquidityMining"] = (MAX_SUPPLY * LIQUIDITY_MINING) / 10000;
        categoryLimit["stakingRewards"] = (MAX_SUPPLY * STAKING_REWARDS) / 10000;
        categoryLimit["reserve"] = (MAX_SUPPLY * RESERVE_ALLOCATION) / 10000;

        // Setup fee discount tiers
        feeDiscountTiers[1000 * 10**18] = 10;   // 1k XPS = 10% discount
        feeDiscountTiers[5000 * 10**18] = 20;   // 5k XPS = 20% discount
        feeDiscountTiers[10000 * 10**18] = 30;  // 10k XPS = 30% discount
        feeDiscountTiers[50000 * 10**18] = 50;  // 50k XPS = 50% discount
        feeDiscountTiers[100000 * 10**18] = 75; // 100k XPS = 75% discount

        // Initial mint for team (vested)
        _mintToCategory("team", teamWallet, categoryLimit["team"]);
    }

    /**
     * @dev Mint tokens for specific category with limits
     */
    function mintToCategory(
        string memory category,
        address recipient,
        uint256 amount
    ) external onlyOwner {
        _mintToCategory(category, recipient, amount);
    }

    function _mintToCategory(
        string memory category,
        address recipient,
        uint256 amount
    ) internal {
        require(recipient != address(0), "Invalid recipient");
        require(
            categoryMinted[category] + amount <= categoryLimit[category],
            "Exceeds category allocation"
        );
        require(totalSupply() + amount <= MAX_SUPPLY, "Exceeds max supply");

        categoryMinted[category] += amount;
        _mint(recipient, amount);

        emit AllocationMinted(category, recipient, amount);
    }

    /**
     * @dev Set contract addresses for staking and liquidity mining
     */
    function setContracts(
        address _stakingContract,
        address _liquidityMiningContract
    ) external onlyOwner {
        stakingContract = _stakingContract;
        liquidityMiningContract = _liquidityMiningContract;
    }

    /**
     * @dev Mint rewards for staking
     */
    function mintStakingRewards(address recipient, uint256 amount) 
        external 
        onlyAuthorized 
    {
        _mintToCategory("stakingRewards", recipient, amount);
    }

    /**
     * @dev Mint rewards for liquidity mining
     */
    function mintLiquidityRewards(address recipient, uint256 amount) 
        external 
        onlyAuthorized 
    {
        _mintToCategory("liquidityMining", recipient, amount);
    }

    /**
     * @dev Calculate fee discount based on XPS balance
     */
    function calculateFeeDiscount(address user) external view returns (uint256) {
        uint256 balance = balanceOf(user);
        uint256 discount = 0;

        // Find highest applicable tier
        if (balance >= 100000 * 10**18) {
            discount = feeDiscountTiers[100000 * 10**18];
        } else if (balance >= 50000 * 10**18) {
            discount = feeDiscountTiers[50000 * 10**18];
        } else if (balance >= 10000 * 10**18) {
            discount = feeDiscountTiers[10000 * 10**18];
        } else if (balance >= 5000 * 10**18) {
            discount = feeDiscountTiers[5000 * 10**18];
        } else if (balance >= 1000 * 10**18) {
            discount = feeDiscountTiers[1000 * 10**18];
        }

        return discount;
    }

    /**
     * @dev Calculate effective fee after XPS discount
     */
    function getEffectiveFee(address user) external view returns (uint256) {
        uint256 discount = this.calculateFeeDiscount(user);
        uint256 effectiveFee = BASE_FEE - ((BASE_FEE * discount) / 100);
        return effectiveFee;
    }

    /**
     * @dev Burn tokens from protocol revenue
     */
    function burnFromRevenue(uint256 amount, string memory reason) external onlyOwner {
        require(amount > 0, "Amount must be greater than 0");
        require(balanceOf(address(this)) >= amount, "Insufficient balance");

        _burn(address(this), amount);
        totalBurned += amount;

        emit TokensBurned(amount, reason);
    }

    /**
     * @dev Emergency burn function for deflation
     */
    function emergencyBurn(uint256 amount) external onlyOwner {
        require(amount > 0, "Amount must be greater than 0");
        require(totalSupply() >= amount, "Insufficient total supply");

        _burn(address(this), amount);
        totalBurned += amount;

        emit TokensBurned(amount, "Emergency deflation");
    }

    /**
     * @dev Get circulating supply (total supply - burned)
     */
    function getCirculatingSupply() external view returns (uint256) {
        return totalSupply();
    }

    /**
     * @dev Get burn rate (percentage of max supply burned)
     */
    function getBurnRate() external view returns (uint256) {
        return (totalBurned * 10000) / MAX_SUPPLY; // Return in basis points
    }

    /**
     * @dev Pause token transfers
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause token transfers
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Override _update instead of _beforeTokenTransfer for v5
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._update(from, to, amount);
    }

    /**
     * @dev Modifier for authorized contracts only
     */
    modifier onlyAuthorized() {
        require(
            msg.sender == stakingContract || 
            msg.sender == liquidityMiningContract || 
            msg.sender == owner(),
            "Not authorized"
        );
        _;
    }

    /**
     * @dev Update fee discount tier
     */
    function updateFeeDiscountTier(uint256 xpsAmount, uint256 discountPercent) 
        external 
        onlyOwner 
    {
        require(discountPercent <= 100, "Discount cannot exceed 100%");
        feeDiscountTiers[xpsAmount] = discountPercent;
    }

    /**
     * @dev Withdraw accidentally sent tokens
     */
    function withdrawToken(address token, uint256 amount) external onlyOwner {
        require(token != address(this), "Cannot withdraw XPS");
        IERC20(token).transfer(owner(), amount);
    }

    /**
     * @dev Get allocation information
     */
    function getAllocationInfo(string memory category) 
        external 
        view 
        returns (uint256 limit, uint256 minted, uint256 remaining) 
    {
        limit = categoryLimit[category];
        minted = categoryMinted[category];
        remaining = limit - minted;
    }
}

